import { useState } from "react"
import { motion, AnimatePresence } from "framer-motion"
import DashboardLayout from "../components/layout/DashboardLayout"
import { Search, BookOpen, ChevronDown, ChevronUp } from "lucide-react"
import { Button } from "../components/ui/button"

const legalTerms = [
  {
    term: "Force Majeure",
    definition:
      "A clause that frees both parties from liability or obligation when an extraordinary event or circumstance beyond the control of the parties occurs.",
    category: "Contract Law",
    examples: [
      "Natural disasters",
      "War or armed conflict",
      "Government actions",
      "Epidemics or pandemics"
    ]
  },
  {
    term: "Indemnification",
    definition:
      "A contractual obligation of one party to compensate the loss incurred by the other party due to the relevant act of the indemnitor or any other party.",
    category: "Contract Law",
    examples: [
      "Legal defense costs",
      "Settlement payments",
      "Third-party claims",
      "Regulatory fines"
    ]
  },
  {
    term: "Intellectual Property",
    definition:
      "A category of property that includes intangible creations of the human intellect, such as patents, trademarks, and copyrights.",
    category: "IP Law",
    examples: [
      "Patents",
      "Trademarks",
      "Copyrights",
      "Trade secrets"
    ]
  },
  {
    term: "Non-Disclosure Agreement",
    definition:
      "A legal contract between at least two parties that outlines confidential material, knowledge, or information that the parties wish to share with one another.",
    category: "Contract Law",
    examples: [
      "Business strategies",
      "Technical specifications",
      "Customer data",
      "Proprietary processes"
    ]
  },
  {
    term: "Term Sheet",
    definition:
      "A non-binding document outlining the basic terms and conditions under which an investment will be made.",
    category: "Investment Law",
    examples: [
      "Valuation",
      "Investment amount",
      "Board composition",
      "Liquidation preferences"
    ]
  },
]

export default function Glossary() {
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedCategory, setSelectedCategory] = useState("All")
  const [expandedTerm, setExpandedTerm] = useState(null)

  const categories = ["All", ...new Set(legalTerms.map((term) => term.category))]

  const filteredTerms = legalTerms.filter((term) => {
    const matchesSearch = term.term
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
    const matchesCategory =
      selectedCategory === "All" || term.category === selectedCategory
    return matchesSearch && matchesCategory
  })

  const toggleTerm = (term) => {
    setExpandedTerm(expandedTerm === term ? null : term)
  }

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="space-y-8"
        >
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Legal Glossary</h1>
            <p className="mt-2 text-lg text-gray-600">
              Search and browse common legal terms and their definitions.
            </p>
          </div>

          {/* Search and Filter */}
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="relative flex-1">
              <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                className="block w-full pl-12 pr-4 py-3 border border-gray-300 rounded-xl leading-5 bg-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm transition-colors"
                placeholder="Search terms..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <select
              className="block w-full sm:w-48 pl-4 pr-10 py-3 text-base border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-xl transition-colors"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              {categories.map((category) => (
                <option key={category} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>

          {/* Terms List */}
          <div className="bg-white shadow-lg rounded-xl divide-y divide-gray-200">
            <AnimatePresence>
              {filteredTerms.map((term, index) => (
                <motion.div
                  key={term.term}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ delay: index * 0.1 }}
                  className="p-6 hover:bg-gray-50 transition-colors"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center">
                        <BookOpen className="h-5 w-5 text-blue-500 mr-3" />
                        <h3 className="text-xl font-semibold text-gray-900">
                          {term.term}
                        </h3>
                      </div>
                      <p className="mt-1 text-sm text-gray-500">{term.category}</p>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => toggleTerm(term)}
                      className="ml-4"
                    >
                      {expandedTerm === term ? (
                        <ChevronUp className="h-5 w-5" />
                      ) : (
                        <ChevronDown className="h-5 w-5" />
                      )}
                    </Button>
                  </div>
                  <div className="mt-4">
                    <p className="text-base text-gray-700">{term.definition}</p>
                  </div>
                  <AnimatePresence>
                    {expandedTerm === term && (
                      <motion.div
                        initial={{ height: 0, opacity: 0 }}
                        animate={{ height: "auto", opacity: 1 }}
                        exit={{ height: 0, opacity: 0 }}
                        transition={{ duration: 0.2 }}
                        className="mt-4"
                      >
                        <div className="bg-blue-50 rounded-lg p-4">
                          <h4 className="text-sm font-medium text-blue-900 mb-2">
                            Examples
                          </h4>
                          <ul className="list-disc list-inside space-y-1">
                            {term.examples.map((example, i) => (
                              <li key={i} className="text-sm text-blue-800">
                                {example}
                              </li>
                            ))}
                          </ul>
                        </div>
                      </motion.div>
                    )}
                  </AnimatePresence>
                </motion.div>
              ))}
            </AnimatePresence>
          </div>

          {filteredTerms.length === 0 && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-12 bg-white rounded-xl shadow-lg"
            >
              <BookOpen className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-lg text-gray-500">No terms found matching your search.</p>
              <p className="text-sm text-gray-400 mt-2">Try adjusting your search or filters.</p>
            </motion.div>
          )}
        </motion.div>
      </div>
    </DashboardLayout>
  )
}
